import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const User = props => (
  <tr>
    <td>{props.exercise.username}</td>
    <td>
      <Link to={"/edit/"+props.exercise.username}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise.username) }}>delete</a>
    </td>
  </tr>
)

export default class CreateUser extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: ''
    }
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    })
  }

  onSubmit(e) {
    console.log('onSubmit begin');
    e.preventDefault();

    const newUser = {
      username: this.state.username
    }

    console.log(newUser);

    axios.post('http://kplumme1-ec2.ddns.net:5000/users/add', newUser)
      .then(res => console.log(res.data));

    this.setState({
      username: ''
    })
  }

    exerciseList() {
    return this.state.exercises.map(currentexercise => {
      return <User exercise={currentexercise} deleteExercise={this.deleteExercise} key={currentexercise._id}/>;
    })
  }

  render() {
    return (
      <div>
        <h3>Create New User</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group"> 
            <label>Username: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.username}
                onChange={this.onChangeUsername}
                />
          </div>
          <div className="form-group">
            <input type="submit" value="Create User" className="btn btn-primary" />
          </div>
        </form>
      </div>
    )
  }
}
